package com.hanzhisoft.signinapp.adapter;import java.util.List;import com.hanzhisoft.signinapp.R;import com.hanzhisoft.signinapp.data.SignRecord;import android.annotation.SuppressLint;import android.app.Activity;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;public class SignInHistoryList extends ArrayAdapter<SignRecord> {	private Context context;	private List<SignRecord> signRecords;	public SignInHistoryList(Context context, int resource,			List<SignRecord> signRecords) {		super(context, resource, signRecords);		this.context = context;		this.signRecords = signRecords;	}	@SuppressLint("ViewHolder")	@Override	public View getView(int position, View convertView, ViewGroup parent) {		LayoutInflater inflater = (LayoutInflater) context				.getSystemService(Activity.LAYOUT_INFLATER_SERVICE);				View view = inflater.inflate(R.layout.item_sign_record_layout , parent, false);		SignRecord record = signRecords.get(position);		TextView addrText = (TextView) view.findViewById(R.id.sign_address);				TextView signDate = (TextView) view.findViewById(R.id.sign_date);		addrText.setText(record.getAddress());		signDate.setText(record.getSignTime());				return view;	}}